import { useState } from "react";

const AddUser = (props) => {
  const [enteredInput, setEnteredInput] = useState({
    username: "",
    age: "",
  });

  const inputsChangeHandler = ({target: {name, value}}) => {
    setEnteredInput((prevInputsObj) => {
        return {...prevInputsObj, [name] : value} 
    })
  };
  return(
  <form>
    <label htmlFor="username">Username</label>
    <input type="text" id="username" value={enteredInput.username} onChang={inputsChangeHandler} />
    <label htmlFor="username">Age (years)</label>
    <input type="number" id="age" value={enteredInput.age} onChang={inputsChangeHandler}/>
    {/* <Button></Button> */}
  </form>
  );
};
export default AddUser;
