import { useState } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import classes from "./AddUser.module.css";
import ErrorModal from "../UI/ErrorModal";

const AddUser = (props) => {
  const [isValidInputs, setIsValidInputs] = useState(true);
  const [isValidAge, setIsValidAge] = useState(true);
  const [error, setError] = useState();

  const [enteredInput, setEnteredInput] = useState({
    username: "",
    age: "",
  });

  const inputsChangeHandler = ({ target: { id, value } }) => {
    // if (enteredInput.age < 1) {
    //   setIsValidAge(false)
    // } else {
    //   setIsValidAge(true)
    // }
    setIsValidInputs(true);
    setEnteredInput((prevInputsObj) => {
      return { ...prevInputsObj, [id]: value };
    });
  };

  const addUserHandler = (e) => {
    e.preventDefault();
    if (
      enteredInput.username.trim().length == 0 ||
      enteredInput.age.trim().length == 0
    ) {
      setError({
        title: "InValid input",
        message: "Please enter a valid name and age (non-empty values).",
      });
      setIsValidInputs(false);
      return;
    }
    if (+enteredInput.age < 1) {
      setIsValidAge(false);
      setError({
        title: "InValid age",
        message: "Please enter a valid age (> 0).",
      });
      return;
    } else {
      setIsValidAge(true);
    }
    props.onAddUser(enteredInput.username, enteredInput.age);
    setEnteredInput({
      username: "",
      age: "",
    });
  };
  console.log(enteredInput);
  return (
    <>
      {error && <ErrorModal title={error.title} message={error.message} />}
      <Card className={`${classes.input} ${!isValidInputs && "invalid"}`}>
        <form onSubmit={addUserHandler}>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            value={enteredInput.username}
            onChange={inputsChangeHandler}
          />
          <label htmlFor="age">Age (Years)</label>
          <input
            type="number"
            id="age"
            value={enteredInput.age}
            onChange={inputsChangeHandler}
          />
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </>
  );
};
export default AddUser;
