import { useState } from "react";

const AddUser = (props) => {
  const [enteredInput, setEnteredInput] = useState({
    username: "",
    age: "",
  });

  const inputsChangeHandler = ({target: {id, value}}) => {
    console.log(id);
    setEnteredInput((prevInputsObj) => {
        return {...prevInputsObj, [id] : value} 
    })
  };
  
  const addUserHandler = e => {
    e.preventDefault();
    props.addUserByInputs()
  }
  console.log(enteredInput);
  return(
  <form onSubmit={addUserHandler}>
    <label htmlFor="username">Username</label>
    <input type="text" id="username" value={enteredInput.username} onChang={inputsChangeHandler} />
    <label htmlFor="age">Age (Years)</label>
    <input type="number" id="age" value={enteredInput.age} onChang={inputsChangeHandler}/>
    {/* <Button></Button> */}
    <button type="submit">Add User</button>
  </form>
  );
};
export default AddUser;
