import { useState } from "react";
import Card from "../UI/Card";

const AddUser = (props) => {
  const [enteredInput, setEnteredInput] = useState({
    username: "k",
    age: "",
  });

  const inputsChangeHandler = ({target: {id, value}}) => {
    setEnteredInput((prevInputsObj) => {
        return {...prevInputsObj, [id] : value} 
    })
  };
  
  const addUserHandler = e => {
    e.preventDefault();
    props.addUserByInputs()
  }
  console.log(enteredInput);
  return(
  <form onSubmit={addUserHandler}>
    <Card>
      <label htmlFor="username">Username</label>
      <input type="text" id="username" value={enteredInput.username} onChange={inputsChangeHandler} />
    </Card>
    <label htmlFor="age">Age (Years)</label>
    <input type="number" id="age" value={enteredInput.age} onChange={inputsChangeHandler}/>
    {/* <Button></Button> */}
    <button type="submit">Add User</button>
  </form>
  );
};
export default AddUser;
