import React, { useState } from 'react';
import AddUser from './components/Users/AddUser';
import Users from './components/Users/Users';


function App() {
  const [users, setUsers] = useState([])

  const addUserByInputs = (inputs) => {
    setUsers(prev=>{
      return  [{...inputs}, ...prev]
    })

  }
  return (
    <div>
      <AddUser addUserByInputs={addUserByInputs}/>
      <Users users={users}/>
    </div>
  );
}

export default App;
