import { useState } from "react";

const AddUser = (props) => {
  const [enteredInput, setEnteredInput] = useState({
    username: "k",
    age: "",
  });

  const inputsChangeHandler = ({target}) => {
    console.log(1, target.name);
    setEnteredInput( {...enteredInput, [target.name] : target.value} 
  )
    // setEnteredInput((prevInputsObj) => {
    //     return {...prevInputsObj, [target.name] : target.value} 
    // })
  };
  
  const addUserHandler = e => {
    e.preventDefault();
    props.addUserByInputs()
  }
  console.log(enteredInput);
  return(
  <form onSubmit={addUserHandler}>
    <label htmlFor="username">Username</label>
    <input type="text" id="username" name="username" value={enteredInput.username} onChang={inputsChangeHandler} />
    <label htmlFor="age">Age (Years)</label>
    <input type="number" id="age" name="age" value={enteredInput.age} onChange={inputsChangeHandler}/>
    {/* <Button></Button> */}
    <button type="submit">Add User</button>
  </form>
  );
};
export default AddUser;
